  selector: 'app-entry-data',
  templateUrl: './entry-data.component.html',
  styleUrls: ['./entry-data.component.css'],
})
export class EntryDataComponent {
    placeholder:string = "email"
    placepass:string = "senha"
    color:string= "blue"
}
